// SPDX-License-Identifier: MIT
pragma solidity >=0.7.0 <0.9.0;

/**
 * @notice I know its preferably to follow EIP-712 standard for signatures, but I wanted to briefly learn how it works
 */
library CryptoSuite {
    function splitSignature(
        bytes memory sig
    ) internal pure returns (uint8 v, bytes32 r, bytes32 s) {
        require(sig.length == 65);

        assembly {
            // first 32bytes
            r := mload(add(sig, 32))

            // next 32bytes
            s := mload(add(sig, 64))

            // next 32bytes
            v := byte(0, mload(add(sig, 96)))
        }

        return (v, r, s);
    }

    function recoverSigner(
        bytes32 message,
        bytes memory m_sig
    ) internal pure returns (address) {
        (uint8 v, bytes32 r, bytes32 s) = splitSignature(m_sig);
        bytes memory m_prefix = "\x19Ethereum Signed Message:\n32";
        bytes32 prefixedHash = keccak256(abi.encodePacked(m_prefix, message));

        return ecrecover(prefixedHash, v, r, s);
    }
}

contract ColdChain {
    enum Mode {
        ISSUER,
        PROVER,
        VERIFIER
    }
    enum Status {
        MANUFACTURED,
        DELIVERING_INTERNATIONAL,
        STORED,
        DELIVERING_LOCAL,
        DELIVERED
    }
    struct Entity {
        address id;
        Mode mode;
        uint[] certificateIds;
    }
    struct Certificate {
        uint id;
        Entity issuer;
        Entity prover;
        bytes signature;
        Status status;
    }
    struct VaccineBatch {
        uint id;
        string brand;
        address manufacturer;
        uint[] certificateIds;
    }

    uint public constant MAX_CERTIFICATIONS = 2;

    uint[] public certificateIds;
    uint[] public vaccineBatchIds;

    mapping(uint => VaccineBatch) public vaccineBatches;
    mapping(uint => Certificate) public certificates;
    mapping(address => Entity) public entities;

    event AddEntityEvent(address entityId, string entityMode);
    event AddVaccineBatchEvent(
        uint vaccineBatchId,
        address indexed manufacturer
    );
    event IssueCertificateEvent(
        address indexed issuer,
        address indexed prover,
        uint certificateId
    );

    function addEntity(address _id, string memory m_mode) public {
        Mode mode = unmarshalMode(m_mode);
        uint[] memory m_certificatesIds = new uint[](MAX_CERTIFICATIONS);
        Entity memory m_entity = Entity(_id, mode, m_certificatesIds);

        entities[_id] = m_entity;

        emit AddEntityEvent(m_entity.id, m_mode);
    }

    function unmarshalMode(string memory m_mode) private pure returns (Mode) {
        bytes32 encodedMode = keccak256(abi.encodePacked(m_mode));
        bytes32 encodedMode0 = keccak256(abi.encodePacked("ISSUER"));
        bytes32 encodedMode1 = keccak256(abi.encodePacked("PROVER"));
        bytes32 encodedMode2 = keccak256(abi.encodePacked("VERIFIER"));

        if (encodedMode == encodedMode0) {
            return Mode.ISSUER;
        } else if (encodedMode == encodedMode1) {
            return Mode.PROVER;
        } else if (encodedMode == encodedMode2) {
            return Mode.VERIFIER;
        }

        revert("received invalid entity mode");
    }

    function addVaccineBatch(
        string memory m_brand,
        address _manufacturer
    ) public returns (uint) {
        uint[] memory m_certificatesIds = new uint[](MAX_CERTIFICATIONS);
        uint id = vaccineBatchIds.length;
        VaccineBatch memory batch = VaccineBatch(
            id,
            m_brand,
            _manufacturer,
            m_certificatesIds
        );

        vaccineBatches[id] = batch;
        vaccineBatchIds.push(id);

        emit AddVaccineBatchEvent(batch.id, batch.manufacturer);
        return id;
    }

    function issueCertificate(
        address _issuer,
        address _prover,
        string memory m_status,
        uint vaccineBatchId,
        bytes memory m_signature
    ) public returns (uint) {
        Entity memory m_issuer = entities[_issuer];
        require(m_issuer.mode == Mode.ISSUER);

        Entity memory m_prover = entities[_prover];
        require(m_prover.mode == Mode.PROVER);

        Status status = unmarshalStatus(m_status);

        uint id = certificateIds.length;
        Certificate memory certificate = Certificate(
            id,
            m_issuer,
            m_prover,
            m_signature,
            status
        );

        certificateIds.push(certificateIds.length);
        certificates[certificateIds.length - 1] = certificate;

        emit IssueCertificateEvent(_issuer, _prover, id);

        return certificateIds.length - 1;
    }

    function unmarshalStatus(
        string memory m_status
    ) private pure returns (Status) {
        bytes32 encodedStatus = keccak256(abi.encodePacked(m_status));
        bytes32 encodedStatus0 = keccak256(abi.encodePacked("MANUFACTURED"));
        bytes32 encodedStatus1 = keccak256(
            abi.encodePacked("DELIVERING_INTERNATIONAL")
        );
        bytes32 encodedStatus2 = keccak256(abi.encodePacked("STORED"));
        bytes32 encodedStatus3 = keccak256(
            abi.encodePacked("DELIVERING_LOCAL")
        );
        bytes32 encodedStatus4 = keccak256(abi.encodePacked("DELIVERED"));

        if (encodedStatus == encodedStatus0) {
            return Status.MANUFACTURED;
        } else if (encodedStatus == encodedStatus1) {
            return Status.DELIVERING_INTERNATIONAL;
        } else if (encodedStatus == encodedStatus2) {
            return Status.STORED;
        } else if (encodedStatus == encodedStatus3) {
            return Status.DELIVERING_LOCAL;
        } else if (encodedStatus == encodedStatus4) {
            return Status.DELIVERED;
        }

        revert("received invalid entity mode");
    }

    function isMatchingSignature(
        bytes32 message,
        uint id,
        address issuer
    ) public view returns (bool) {
        Certificate memory m_certificate = certificates[id];
        require(m_certificate.issuer.id == issuer);

        address recoveredSigner = CryptoSuite.recoverSigner(
            message,
            m_certificate.signature
        );

        return recoveredSigner == m_certificate.issuer.id;
    }
}
